import React from "react"
import PropTypes from "prop-types"

function DisplayData(props) {
    let categoryList = props.availableCategories.map((category) => {
        return (<p key={category.id} onClick={props.clickCategoryCard}>{category.id}</p>)
    })

    let artistList = props.featuredArtists.map((artist, index) => {
        return (<p key={artist} onClick={() => props.clickArtist(index)} className={props.checkedArtists[index] ? "checked" : null}>{artist}</p>)
    })

    return (
        <div>
            {props.currentUser && <p className="userWelcome">Hello {props.currentUser}!</p>}

            {props.selectedCategory && (
                <div>
                    <p className="stepInstructions">Choose a category:</p>
                    {categoryList}
                </div>)
            }

            {props.checkedArtists.filter(i => i).length > 0 && <button onClick={props.submitSelectedArtist}>Generate</button>}

            {props.featuredArtists
                ? ( <div>
                        <p className="stepInstructions">{props.selectedCategory} Artists:</p>
                        {artistList}
                    </div>)
                : 'No artists yet'
            }
        </div>
    )
}

DisplayData.propTypes = {
    currentUser: PropTypes.string,
    availableCategories: PropTypes.arrayOf(PropTypes.object).isRequired,
    selectedCategory: PropTypes.string.isRequired,
    categoryPlaylists: PropTypes.arrayOf(PropTypes.object).isRequired,
    featuredArtists: PropTypes.array.isRequired,
    checkedArtists: PropTypes.array.isRequired,
    clickArtist: PropTypes.func.isRequired,
    submitSelectedArtist: PropTypes.func.isRequired,
    clickCategoryCard: PropTypes.func.isRequired
}

export default DisplayData
