import React, {useState, useEffect} from "react"
import SpotifyWebApi from "spotify-web-api-js"
import LoginButton from "./LoginButton"

const spotifyWebApi = new SpotifyWebApi();

/*
    --Display current username
    --Fetch and display list of Categories
    --Save user category choice
    --Fetch category playlists
    Get top artists from each
    Save to list of artist
        - insert similar artist into list
    Display list of artists
*/

function SpotifyData() {
    let [token, setToken] = useState(false) 
    let [currentUser, setCurrentUser] = useState()
    let [availableCategories, setAvailableCategories] = useState([])
    let [selectedCategory, setSelectedCategory] = useState("")
    let [categoryPlaylists, setCategoryPlaylists] = useState([])
    let [featuredArtists, setfeaturedArtists] = useState([])
    let [artistListCompleted, setArtistListCompleted] = useState(false)
    let [checkedArtists, setCheckArtists] = useState([])

    function getMe() {
        spotifyWebApi.getMe()
            .then((response) => {
                if (response) {
                    setCurrentUser(response.display_name)
                } 
        });
    }

    function getCategories() {
        spotifyWebApi.getCategories()
            .then((response) => {
                if (response) {
                    setAvailableCategories(response.categories.items)
                } 
        });
    }

    function getCategory(id) {
        spotifyWebApi.getCategory(id)
            .then((response) => {
                setSelectedCategory(response.name);
                getCategoryPlaylists(response.id);
        });
    }

    function getCategoryPlaylists(id) {
        spotifyWebApi.getCategoryPlaylists(id)
            .then((response) => {
                setCategoryPlaylists(response.playlists.items);
        });
    }

    function getPlaylistTracks(id) {
        const lim = 10;

        spotifyWebApi.getPlaylistTracks(id, {limit: lim}) // Only get 5 songs
            .then((response) => {
                let startingTracks = response.items;

                for (let i = 0; i < lim; i++) {
                    let thisArtist = startingTracks[i].track.artists[0].name;

                    setfeaturedArtists((prevArray) => {
                        if (prevArray.includes(thisArtist)) {
                            return prevArray
                        } else {
                            // Make a boolean array of the same length
                            setCheckArtists((prevArray) => [...prevArray, false])
                            let updatedList = [...prevArray, thisArtist]
                            return updatedList
                        }
                    })
                }
        });
    }

    function saveToken(token) {
        spotifyWebApi.setAccessToken(token);
        setToken(token);
    }

    function clickCard(event) {
        getCategory(event.target.innerText)
    }

    function clickArtist(index) {
        setCheckArtists((prevArray) => {
            console.log(`Flipping checkArists[${index}]`)
            let newArray = [...prevArray]
            newArray[index] = !prevArray[index]
            return newArray
        })
    }

    useEffect(() => {
        if (categoryPlaylists.length > 0) {
            for (let i = 0, l = categoryPlaylists.length; i < l; i++) {
                getPlaylistTracks(categoryPlaylists[i].id)
            }
            return
        }
    }, [categoryPlaylists])

    useEffect(() => {
        if (token) {
            getMe()
            getCategories()
            return
        }
    }, [token])

    let categoryList = availableCategories.map((category) => {
        return (<p key={category.id} onClick={clickCard}>{category.id}</p>)
    })

    let artistList = featuredArtists.map((artist, index) => {
        return (<p key={artist} onClick={() => clickArtist(index)} className={checkedArtists[index] ? "checked" : null}>{artist}</p>)
    })

    return (
        <div>
            {!token && <LoginButton saveToken={saveToken} />}
            {currentUser && <p>Hello {currentUser}!</p>}

            {selectedCategory
                ? <p>Selected: {selectedCategory}</p>
                : categoryList
            }

            {featuredArtists
                ? artistList
                : 'No artists yet'
            }

        </div>
    )
}

export default SpotifyData
