import React, {useState, useEffect} from "react"
import parseCallBack from "./parseCallback"

function LoginButton(props) {
    const [promptLogin, setPromptLogin] = useState(true);

    useEffect(() => {
        const validToken = parseCallBack(window.location.href);

        if (validToken) {
            props.saveToken(validToken)
            window.location.hash = '' // App will rerender with just .location
        } else {
            setPromptLogin(true);
        }
    }, [])

    function connectSpotify() {
        /*
        // Redirect to server link
        //window.location = BACKTRACK_LINK; // process.env.REACT_APP_BACKTRACK_LINK

        // Redirect to Spotify
        // var stateKey = 'spotify_auth_state';
        // var state = generateRandomString(16);
        //localStorage.setItem(stateKey, state);

        // https://developer.spotify.com/documentation/general/guides/scopes/
        var scope = 'user-read-private user-read-email user-read-playback-state';

        var url = 'https://accounts.spotify.com/authorize';
        url += '?response_type=token';
        url += '&client_id=' + encodeURIComponent(CLIENT_ID);
        url += '&scope=' + encodeURIComponent(scope);
        url += '&redirect_uri=' + encodeURIComponent(SERVER_URL + '/callback');
        //url += '&state=' + encodeURIComponent(state);

        window.location = url;
        */
       const backtrackLink = 'http://localhost:8888'
        window.location = backtrackLink;
    }

    return (
        <div className="accountStatus">
            {promptLogin && <button onClick={connectSpotify}>Connect Account</button>}
        </div>
    )
}

export default LoginButton
