import React from "react"

function ArtistSelection(props) {
    let cardList = props.list.map((artist, index) => {
        let img = artist.profile
        if (artist.profile === '') {
            img = 'https://developers.google.com/maps/documentation/streetview/images/error-image-generic.png';
        }

        return (<div key={index}
                    onClick={() => props.handleClick(index)}
                    className={props.checkedArtists[index] ? "checked card" : "card"}>
                    <img src={img} alt="Artist profile"></img>
                    <p>{artist.name}</p>
                </div>)
    })

    return (
        <div className="cardGrid">
            {cardList}
        </div>
    )
}

export default ArtistSelection;
